import React, { useState } from 'react';
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'
import Modal from 'react-bootstrap/Modal'
import './input-form.styles.scss';

// import Popup1 from '../../components/popup1/popup1.component';

const axios = require('axios')

class InputForm extends React.Component {
  constructor() {
    super();

      this.state={
        ItemID: '10',
        RestaurantID: 'zakir',
        name: '',
        description: '',
        category: '',
        availability: 'Yes',
        price: '',
        imageurl: '',

        addModalShow: false,
        popupshow: false,
      }  
  }

  updateName(event) {
    this.setState({name: event.target.value})
  }

  updatePrice(event) {
    this.setState({price: event.target.value})
  }

  updateDescription(event) {
    this.setState({description: event.target.value})
  }

  updateCategory(event) {
    this.setState({category: event.target.value})
  }

  updateImage(event) {
    this.setState({imageurl: event.target.value})
  }

  falsePopupshow() {
    this.setState({popupshow: false})
  }

  truePopupshow() {
    this.setState({popupshow: true})
  }

  handleSubmit = () => {
    let details = {
      "ItemID": "10",
      "RestaurantID": "zakir",
	    "name": this.state.name,
	    "description": this.state.description,
	    "category": this.state.category,
	    "availability": "Yes",
	    "price": this.state.price,
	    "imageurl": this.state.imageurl
      }
    axios.post('/menu/add', details)
      .then((res) => window.location.reload())
      .catch(err => console.log(err))
  }

  // check(event) {
  //   const [show, setShow] = useState(false);
  //   const handleClose = () => setShow(false);
  //   const handleShow = () => setShow(true);
  // }

  render() {
    let addModalClose =() => this.setState({addModalShow: false})
    // const [show, setShow] = useState(false);

    // const handleClose = () => setShow(false);
    // const handleShow = () => setShow(true);
    // const [show, setShow] = useState(false);

    return (
      <div>
        <Form onSubmit = {this.handleSubmit}>
            <Form.Group controlId="Name">
                <Form.Label>Item Name</Form.Label>
                <Form.Control type="name"
                              className = 'itemname'
                              placeholder="Enter item name"
                              value = {this.state.name}
                              onChange = {this.updateName.bind(this)} />
            </Form.Group>

            <Form.Group controlId="Price">
                <Form.Label>Price</Form.Label>
                <Form.Control type="number"
                              placeholder="Enter price in pkr"
                              value ={this.state.price}
                              onChange = {this.updatePrice.bind(this)} />
            </Form.Group>

            <Form.Group controlId="Description">
                <Form.Label>Description</Form.Label>
                <Form.Control type="name"
                              placeholder="Enter item description"
                              value ={this.state.description}
                              onChange = {this.updateDescription.bind(this)} />
            </Form.Group>

            <Form.Group controlId="Category">
                <Form.Label>Category</Form.Label>
                <Form.Control type="name"
                              placeholder="Enter item category"
                              value ={this.state.category}
                              onChange = {this.updateCategory.bind(this)} />
            </Form.Group>

            <Form.Group controlId="Image">
                <Form.Label>Image</Form.Label>
                <Form.Control variant="primary" type="name"
                              placeholder="Enter image url"
                              value ={this.state.imageurl}
                              onChange = {this.updateImage.bind(this)} />
            </Form.Group>

            <Button variant="primary" type="submit">
                Add Item
            </Button>

            {/* <Button variant="primary" onClick={()=> this.setState({show: true})}> */}
            <Button variant="primary" onClick = {this.truePopupshow}>
                CHECK
            </Button>

            {/* <Popup1
              show={this.state.addModalShow}
              onHide={addModalClose}
            /> */}

            <Modal show={this.state.show}>
              <Modal.Header closeButton>
                <Modal.Title>Modal heading</Modal.Title>
              </Modal.Header>
              <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
              <Modal.Footer>
                <Button variant="secondary" onClick = {this.falsePopupshow}>
                  Close
                </Button>
                <Button variant="primary">
                  Save Changes
                </Button>
              </Modal.Footer>
            </Modal>

            {/* <Modal show={show} onHide={check.handleClose}>
              <Modal.Header closeButton>
                <Modal.Title>Modal heading</Modal.Title>
              </Modal.Header>
              <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
              <Modal.Footer>
                <Button variant="secondary" onClick={handleClose}>
                  Close
                </Button>
                <Button variant="primary" onClick={handleClose}>
                  Save Changes
                </Button>
              </Modal.Footer>
            </Modal> */}


        </Form>
      </div>
    );
  }
}

export default InputForm;


