import React from 'react';
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'
import './input-form.styles.scss';

class InputForm extends React.Component {
  constructor() {
    super();

      this.state={
        ItemID: '',
        RestaurantID: '',
        name: '',
        description: '',
        category: '',
        availability: '',
        price: '',
        imageurl: ''
      }  
  }

  // "ItemID": "9",
	// "RestaurantID": "zakir",
	// "name": "Garlic Naan",
	// "description": "Garlic Naan",
	// "category": "Tandoor",
	// "availability": "Yes",
	// "price": 40,
	// "imageurl": "https://d1alt1wkdk73qo.cloudfront.net/images/guide/68e69ae60710452b9a751f5b25a959b7/640x478_ac.jpg"


  changeHandler = (e) => {
    this.setState({ [e.target.name]: e.target.value })
  }

  submitHandler = (e) => {
    e.preventDefault()
    console.log(this.state)
  }

  render() {
    const { name, description, category, price, imageurl } = this.state
    return (
      <div>
        <form onSubmit={this.submitHandler}>
            <group controlId="Name">
                <label>Item Name</label>
                <control type="text"
                              name = "name"
                              placeholder="Enter item name"
                              value ={this.state.name}
                              onChange = {this.changeHandler}
                              />
            </group>

            <group controlId="Price">
                <label>Price</label>
                <control type="number"
                              placeholder="Enter price in pkr"
                              value ={price}
                              onChange = {this.changeHandler} />
            </group>

            <group controlId="Description">
                <label>Description</label>
                <control type="name"
                              placeholder="Enter item description"
                              value ={description}
                              onChange = {this.changeHandler} />
            </group>

            <group controlId="Category">
                <label>Category</label>
                <control type="name"
                              placeholder="Enter item category"
                              value ={category}
                              onChange = {this.changeHandler} />
            </group>

            <group controlId="Image">
                <label>Image</label>
                <control type="name"
                              placeholder="Enter image url"
                              value ={imageurl}
                              onChange = {this.changeHandler} />
            </group>
            {/* <Form.Group controlId="formBasicCheckbox">
                <Form.Check type="checkbox" label="Check me out" />
            </Form.Group> */}
            {/* <Button variant="primary" type="submit" onChange={this.handleSubmit}>
                Submit
            </Button> */}
            <button type="submit">
                Submit
            </button>
        </form>
      </div>
    );
  }
}

export default InputForm;




