import React from 'react';
import { connect } from 'react-redux';
// import {compose} from 'redux';
import CustomButton from '../custom-button/custom-button.component';
import { addItem } from '../../redux/cart/cart.actions';
// import { BrowserRouter, Route, Switch } from "react-router-dom";
import { withRouter } from 'react-router-dom';
import './collection-item.styles.scss';
import {NotificationContainer, NotificationManager} from 'react-notifications';
// import { Link } from 'react-router-dom';

const CollectionItem = ({ item, addItem, history, match }) => {
  const { name, price, imageurl } = item;
  let Click = () => {
    addItem(item);
    createNotification('info')
  }
  let createNotification = (type) => {
    return () => {
      switch (type) {
        case 'info':
          NotificationManager.info('Info message');
          break;
        case 'success':
          NotificationManager.success('Success message', 'Title here');
          break;
        case 'warning':
          NotificationManager.warning('Warning message', 'Close after 3000ms', 3000);
          break;
        case 'error':
          NotificationManager.error('Error message', 'Click me!', 5000, () => {
            alert('callback');
          });
        break
        default : NotificationManager.error('Error message', 'Click me!', 5000, () => {
          alert('callback');
        });
      }
      };
    }

  return (
    <div className='collection-item'>
      <div
        className='image'
        style={{
          backgroundImage: `url(${imageurl})`
        }}
      />
      <div className='collection-footer'>
        <span className='name'>{name}</span>
        <span className='price'>Rs{price}</span>
      </div>
      {localStorage.email ?(
      <CustomButton  onClick = {Click()} inverted>
        Add to cart
      </CustomButton>):
      (<CustomButton onClick={()=>  history.push('/login') }   >
        Add to cart
      </CustomButton>
      )
      }

      <NotificationContainer/>
    </div>
  );
};

const mapDispatchToProps = dispatch => ({
  addItem: item => dispatch(addItem(item))
});



export default withRouter(connect(null, mapDispatchToProps)(CollectionItem))
