import React from 'react';
import { withRouter } from 'react-router-dom';
import './new-order-item.styles.scss';

const axios = require('axios')

const OrderItem = ({ OrderID, items, totalPrice, status, Type, userID,  history, linkUrl, match }) => {
  let showItems = items
  console.log(showItems)
  return(
    <>
        <td className = 'font'>{OrderID}</td>
        <td>{items.map(item => <span>{item}{ }</span>)}</td>
        {/* <td className = 'font'>{items}</td> */}
        <td className = 'font'>{totalPrice}</td>
        <td className = 'font'>{Type}</td>
        <td className = 'font'>{userID}</td>
        <td className = 'font'>{status}</td>
        <td><span className = 'new-accept' onClick ={
          () => {
            let change = {
              "OrderID": OrderID,
              "update": {
                  "status": "Pending"
              }
            }
            axios.post('/orders/update', change)
              .then(() => window.location.reload())
              .catch(err => console.log(err))
          }
        }>Accept</span>
        <span className = 'new-decline' onClick = {
          () => {
            let change = {
              "OrderID": OrderID,
              "update": {
                  "status": "Declined"
              }
            }
            axios.post('/orders/update', change)
              .then((res) => console.log(res))
              .catch(err => console.log(err))
          }}>Decline</span></td>

    </>
  )
};

export default withRouter(OrderItem);
