import React from 'react';

import OrderItem from '../order-item copy/order-item.component';
import Table from 'react-bootstrap/Table';

import './orderview.styles.scss';

class OrderView extends React.Component {
  constructor() {
    super();

      this.state={
      sections: []
      }

  }

  componentDidMount(){
    fetch('/orders/all')
    .then(res=> res.json())
    .then(sections => this.setState({sections}))
  }

  render() {
    return (      
        <Table responsive>
          <thead>
            <tr>
              <th>#</th>
              <th>Order No.</th>
              <th>Total Price</th>
              <th>Order Status</th>
              <th>Order Type</th>
            </tr>
          </thead>
          <tbody>
          {this.state.sections.map(({ id, ...otherSectionProps }) => (
            <tr className="orders">
              <OrderItem key={id} {...otherSectionProps} />
            </tr>
          ))}
          </tbody>
        </Table>
    );
  }
}

export default OrderView;
