import React from 'react';
import { connect } from 'react-redux';
import Modal from 'react-bootstrap/Modal'
import Button from 'react-bootstrap/Button'

import CustomButton from '../custom-button/custom-button.component';
import { addItem } from '../../redux/cart/cart.actions';

import './collection-item.styles.scss';

const CollectionItem = ({ item, addItem }) => {
  const { name, price, imageurl } = item;

  function handleSubmit(event) {
    event.preventDefault()
  }

  function refresh(event) {
    window.location.reload();
  }
  return (
    <div className='collection-item'>
      <div
        className='image'
        style={{
          backgroundImage: `url(${imageurl})`
        }}
      />
      <div className='collection-footer'>
        <span className='name'>{name}</span>
        <span className='price'>PKR {price}</span>
      </div>
      <CustomButton onClick={() => addItem(item)} inverted>
        Add to cart
      </CustomButton>

      <Modal show={this.state.popupshow}>
        <Modal.Header closeButton onClick={()=> this.setState({popupshow: false})}>
          <Modal.Title> {this.state.text} </Modal.Title>
        </Modal.Header>
        {/* <Modal.Body>The new item has been added to restaurant menu!</Modal.Body> */}
        <Modal.Body> {this.state.text2} </Modal.Body>
        <Modal.Footer>
          {/* <Button variant="primary" onClick={()=> this.setState({popupshow: false})}> */}
          <Button variant="primary" onClick={this.refresh}>
            Ok
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

const mapDispatchToProps = dispatch => ({
  addItem: item => dispatch(addItem(item))
});

export default connect(
  null,
  mapDispatchToProps
)(CollectionItem);
