import './sign-in2.styles.scss';

class SignIn extends React.Component {

  constructor() {
    super();

      this.state={
        email: '',
        password: '',

        popupshow: false,
        text: 'Item Added',
        text2: 'Item has been added to menu!'
      }  
  }

  updateEmail(event) {
    this.setState({email: event.target.value})
  }

  updatePassword(event) {
    this.setState({password: event.target.value})
