import React from "react";
import './availability.styles.scss';

import AvailItem from '../../components/avail-item/avail-item.component';


class Availability extends React.Component {

    constructor() {
      super();

        this.state={
        collection: [],

        }
    }

    componentDidMount(){

      fetch(`/menu/all`)
        .then(res=> res.json())
        .then(collection => this.setState({collection}))
    }

    render() {

    return (

      <div className='collection-page'>
      <h2 className='title'>Menu Availability</h2>
      <div className='items'>
        {this.state.collection.map(item => (
          <AvailItem key={item.ItemId} item={item} />
        ))}
      </div>
    </div>
    );
    }


  }

  export default Availability
