import React, { useState } from 'react';
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'
import Modal from 'react-bootstrap/Modal'
import InputGroup from 'react-bootstrap/InputGroup'
import './input-form.styles.scss';

const axios = require('axios')

class InputForm extends React.Component {
  constructor() {
    super();

      this.state={
        ItemID: '11',
        RestaurantID: 'zakir',
        name: '',
        description: '',
        category: '',
        availability: 'Yes',
        price: '',
        imageurl: '',

        popupshow: false,
        text: 'Item Added',
        text2: 'Item has been added to menu!',

        validated: false,
        setValidated: false
      }  
  }

  updateName(event) {
    this.setState({name: event.target.value})
  }

  updatePrice(event) {
    this.setState({price: event.target.value})
  }

  updateDescription(event) {
    this.setState({description: event.target.value})
  }

  updateCategory(event) {
    this.setState({category: event.target.value})
  }

  updateImage(event) {
    this.setState({imageurl: event.target.value})
  }

  handleSubmit = (event) => {
    event.preventDefault();
    let details = {
      // "ItemID": "11",
      "RestaurantID": "zakir",
	    "name": this.state.name,
	    "description": this.state.description,
	    "category": this.state.category,
	    "availability": "Yes",
	    "price": this.state.price,
	    "imageurl": this.state.imageurl
      }
    axios.post('/menu/add', details)
      .then((res) => this.setState({text2: 'Item has been added to menu!'}), this.setState({popupshow: true}))
      .catch(err => this.setState({text: 'Error'}), this.setState({text2: 'Could not add item to menu!'}), this.setState({popupshow: true}))
  }

  refresh = (event) => {
    window.location.reload();
  }

  // handleSubmit = (event) => {
  //   const form = event.currentTarget;
  //   if (form.checkValidity() === false) {
  //     event.preventDefault();
  //     event.stopPropagation();
  //   }

  render() {
    // function FormExample() {
    //   const [validated, setValidated] = useState(false);

    //   const handleSubmit = (event) => {
    //     const form = event.currentTarget;
    //     if (form.checkValidity() === false) {
    //       event.preventDefault();
    //       event.stopPropagation();
    //     }

    //     setValidated(true);
    //   };
    return (
      <div className = 'check'>
        <Form noValidate validated={this.validated} onSubmit = {this.handleSubmit}>
            {/* <Form.Group controlId="Name"> */}
            <Form.Group md="4" controlId="validationCustom01">
                <Form.Label>Item Name</Form.Label>
                <InputGroup>
                  <InputGroup.Prepend>
                    <InputGroup.Text ></InputGroup.Text>
                  </InputGroup.Prepend>
                  <Form.Control type="text"
                                placeholder="Enter item name"
                                required
                                value = {this.state.name}
                                onChange = {this.updateName.bind(this)} />
                  <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
                </InputGroup>
            </Form.Group>

            <Form.Group controlId="Price">
                <Form.Label>Price</Form.Label>
                <Form.Control type="number"
                              placeholder="Enter price in pkr"
                              required
                              value ={this.state.price}
                              onChange = {this.updatePrice.bind(this)} />
            </Form.Group>

            <Form.Group controlId="Description">
                <Form.Label>Description</Form.Label>
                <Form.Control type="name"
                              placeholder="Enter item description"
                              required
                              value ={this.state.description}
                              onChange = {this.updateDescription.bind(this)} />
            </Form.Group>

            <Form.Group controlId="Category">
                <Form.Label>Category</Form.Label>
                <Form.Control type="name"
                              placeholder="Enter item category"
                              required
                              value ={this.state.category}
                              onChange = {this.updateCategory.bind(this)} />
            </Form.Group>

            <Form.Group controlId="Image">
                <Form.Label>Image</Form.Label>
                <Form.Control variant="primary" type="url"
                              placeholder="Enter image url"
                              required
                              value ={this.state.imageurl}
                              onChange = {this.updateImage.bind(this)} />
                <Form.Control.Feedback type="invalid">
                  Please choose a username.
                </Form.Control.Feedback>
            </Form.Group>

            <Button variant="primary" type="submit">
                Add Item
            </Button>

            <Modal show={this.state.popupshow}>
              <Modal.Header closeButton onClick={()=> this.setState({popupshow: false})}>
                <Modal.Title> {this.state.text} </Modal.Title>
              </Modal.Header>
              {/* <Modal.Body>The new item has been added to restaurant menu!</Modal.Body> */}
              <Modal.Body> {this.state.text2} </Modal.Body>
              <Modal.Footer>
                {/* <Button variant="primary" onClick={()=> this.setState({popupshow: false})}> */}
                <Button variant="primary" onClick={this.refresh}>
                  Ok
                </Button>
              </Modal.Footer>
            </Modal>

        </Form>
      </div>
    );
  }
}

export default InputForm;
