import React from "react";
// import ReactDOM from "react-dom";
import './collection.styles.scss';
import Button from 'react-bootstrap/Button'
import Modal from 'react-bootstrap/Modal'

import CollectionItem from '../../components/collection-item/collection-item.component';


class Collection extends React.Component {

    constructor(props) {
      super(props);

        this.state={
        collection: [],
        popupshow: false
        }
    }

    refresh = (event) => {
      window.location.reload();
    }

    componentDidMount( )
    {  
     //console.log(`This is match ${this.props.match.params.id}`)
      fetch(`/menu/find/${this.props.match.params.id}`)
        .then(res=> res.json())
        .then(collection => this.setState({collection}))
    }

    render() {
      return (
        <div className='collection-page'>
          <h2 className='title'>{this.props.match.params.id} </h2>
          <div className='items'>
            {this.state.collection.map(item => (
              <CollectionItem key={item.ItemId} item={item} />
            ))}
          </div>

          <Modal show={this.state.popupshow}>
            <Modal.Header closeButton onClick={()=> this.setState({popupshow: false})}>
              <Modal.Title> Item added! </Modal.Title>
            </Modal.Header>
            {/* <Modal.Body>The new item has been added to restaurant menu!</Modal.Body> */}
            <Modal.Body> Item has been added to Cart! </Modal.Body>
            <Modal.Footer>
              {/* <Button variant="primary" onClick={()=> this.setState({popupshow: false})}> */}
              <Button variant="primary" onClick={this.refresh}>
                Ok
              </Button>
            </Modal.Footer>
          </Modal>

        </div>
      );
    }


  }

  export default Collection
