import React from 'react';
import { connect } from 'react-redux';

import CollectionItem from '../../components/collection-item/collection-item.component';

import { selectCollection } from '../../redux/shop/shop.selectors';

import './collection.styles.scss';


class Collection extends React.Component {
  constructor() {
    super();

      this.state={
      collection: []
      }

  }

  componentDidMount(match){
    fetch(`/menu/find/${match.params.collectionId}`)
    .then(res=> res.json())
    .then(collection => this.setState({collection}))
  }

  render() {
    return (
      <div className='collection-page'>
      <h2 className='title'>Zakir</h2>
      <div className='items'>
        {this.state.collection.map(item => (
          <CollectionItem key={item.ItemId} item={item} />
        ))}
      </div>
    </div>
    );
  }
}





const CollectionPage = () => {

  <Collection />

  return (

    <div className='collection-page'>
    <h2 className='title'>Zakir</h2>
    <div className='items'>
      {this.state.collection.map(item => (
        <CollectionItem key={item.ItemId} item={item} />
      ))}
    </div>
  </div>
  );




};

// const mapStateToProps = (state, ownProps) => ({
//  // collection: fetch(`/menu/find/${ownProps.match.params.collectionId}`)
//    //.then(res=> res.json())(state)


// });

export default connect(null)(CollectionPage);
