  //    } 


  return(
  <div className='collection-preview'>
    <h1 className='title'> {items[0].category} </h1>
    <div className='preview'>
      {items.map((item) => (
          <CollectionItem key={item.id} item={item} />
        ))}
    </div>
  </div>
  );
};

export default CollectionPreview;
