import React, { Component } from "react";
import { Link } from "react-router-dom";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { loginUser } from "../../redux/actions/authActions";
import classnames from "classnames";
import Form from 'react-bootstrap/Form'

class Login extends Component {
  constructor() {
    super();
    this.state = {
      email: "",
      password: "",
      isRest: false,
      errors: {}
    };
    this.handleChecked = this.handleChecked.bind(this);
  }

componentDidMount() {
  // If logged in and user navigates to Login page, should redirect them to dashboard
  if (this.props.auth.isAuthenticated) {
    if(!localStorage.isRest){
      //console.log(this.props.location.pathname)
      this.props.history.push('/');}
     else {
      this.props.history.push("/restaurant");
    }
  }
}

handleChecked () {
  this.setState({isRest: !this.state.isRest});
}

UNSAFE_componentWillReceiveProps(nextProps) {
    if (nextProps.auth.isAuthenticated) {
      if(!localStorage.isRest){
        this.props.history.push("/");
      }
      else{
        this.props.history.push("/restaurant");
      }
       // push user to homepage when they login
    }
if (nextProps.errors) {
      this.setState({
        errors: nextProps.errors
      });
    }
  }
onChange = e => {
    this.setState({ [e.target.id]: e.target.value });
  };
onSubmit = e => {
    e.preventDefault();
const userData = {
      email: (this.state.email).toLowerCase(),
      password: this.state.password,
      isRest: this.state.isRest
    };
this.props.loginUser(userData); // since we handle the redirect within our component, we don't need to pass in this.props.history as a parameter
  };
render() {
    const { errors } = this.state;
    // console.log(this.state.isRestuarant)
return (
      <div>
      <div className = 'check'>
        <h1>Add Item to Menu</h1>

        <Form onSubmit = {this.handleSubmit}>
          <Form.Group controlId="ItemID">
                <Form.Label>Item ID</Form.Label>
                <Form.Control type="name"
                              className = 'itemid'
                              placeholder="Enter item ID"
                              required
                              value = {this.state.ItemID}
                              onChange = {this.updateID.bind(this)} />
            </Form.Group>

            <Form.Group controlId="Name">
                <Form.Label>Item Name</Form.Label>
                <Form.Control type="name"
                              className = 'itemname'
                              placeholder="Enter item name"
                              required
                              value = {this.state.name}
                              onChange = {this.updateName.bind(this)} />
            </Form.Group>

            <Form.Group controlId="Price">
                <Form.Label>Price</Form.Label>
                <Form.Control type="name"
                              placeholder="Enter price in pkr"
                              pattern="[0-9]*"
                              // className = 'number'
                              required
                              value ={this.state.price}
                              onChange = {this.updatePrice.bind(this)} />
            </Form.Group>

            <Form.Group controlId="Description">
                <Form.Label>Description</Form.Label>
                <Form.Control type="name"
                              placeholder="Enter item description"
                              required
                              value ={this.state.description}
                              onChange = {this.updateDescription.bind(this)} />
            </Form.Group>

            <Form.Group controlId="Category">
                <Form.Label>Category</Form.Label>
                <Form.Control type="name"
                              placeholder="Enter item category"
                              required
                              value ={this.state.category}
                              onChange = {this.updateCategory.bind(this)} />
            </Form.Group>

            <Form.Group controlId="Image">
                <Form.Label>Image</Form.Label>
                <Form.Control
                              type="name"
                              placeholder="Enter image url"
                              required
                              value ={this.state.imageurl}
                              onChange = {this.updateImage.bind(this)} />
            </Form.Group>

            <Button variant="primary" type="submit" block>
                Add Item
            </Button>
        </Form>
      </div>

      <div className="container">
        <div style={{ marginTop: "4rem" }} className="row">
          <div className="col s8 offset-s2">
            <Link to="/" className="btn-flat waves-effect">
              <i className="material-icons left">keyboard_backspace</i> Back to
              home
            </Link>
            <div className="col s12" style={{ paddingLeft: "11.250px" }}>
              <h4>
                <b>Login</b> below
              </h4>
              <p className="grey-text text-darken-1">
                Don't have an account? <Link to="/register">Register</Link>
              </p>
            </div>
            <form noValidate onSubmit={this.onSubmit}>
              <div className="input-field col s12">
                <input
                  onChange={this.onChange}
                  value={this.state.email}
                  error={errors.email}
                  id="email"
                  type="email"
                  className={classnames("", {
                    invalid: errors.email || errors.emailnotfound
                  })}
                />
                <label htmlFor="email">Email</label>
                <span className="red-text">
                  {errors.email}
                  {errors.emailnotfound}
                </span>
              </div>
              <div className="input-field col s12">
                <input
                  onChange={this.onChange}
                  value={this.state.password}
                  error={errors.password}
                  id="password"
                  type="password"
                  className={classnames("", {
                    invalid: errors.password || errors.passwordincorrect
                  })}
                />
                <label htmlFor="password">Password</label>
                <span className="red-text">
                  {errors.password}
                  {errors.passwordincorrect}    
                </span>
              </div>
              <p style ={{ paddingLeft: "11.250px" }}>
              <label>
                <input type="checkbox" onChange={ this.handleChecked }/>
                <span>Login as Restuarant?</span>
              </label>
              </p>
              <div className="col s12" style={{ paddingLeft: "11.250px" }}>
                <button
                  style={{
                    width: "150px",
                    borderRadius: "3px",
                    letterSpacing: "1.5px",
                    marginTop: "1rem"
                  }}
                  type="submit"
                  className="btn btn-large waves-effect waves-light hoverable blue accent-3"
                >
                  Login
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
      </div>
    );
  }
}

Login.propTypes = {
  loginUser: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired
};
const mapStateToProps = state => ({
  auth: state.auth,
  errors: state.errors
});
export default connect(
  mapStateToProps,
  { loginUser }
)(Login);
